import React, { useState, useEffect } from "react";

function App() {
  const [Items, setItems] = useState([]);
  const [cart, setCart] = useState([]);
  const [cartTotal, setCartTotal] = useState(0);
  useEffect(() => {
    // the total is a function to count the prices are added  below
    total();
}, [cart]);
  useEffect(()=>{button()}, [])

  const total = () => {
    let totalVal = 0;
    for (let i = 0; i < cart.length; i++) {
    totalVal += cart[i].price * cart[i].quantity;
    }
    //setting the state
    setCartTotal(totalVal.toFixed(2));
};


  let button = () => {
    fetch("http://localhost:8081/").then((response) => {
      if (!response.ok) {
        throw new Error("err: ${response.status}");
      }
      response.json().then((data) => {
        console.log(data);
        let tmp = [];
        for (let i of data) {
          tmp.push(i);
        }
        setItems(tmp)
      });
    });
  };


  let cardArray = Items.map((item) => (
    <div key={item["title"]}>
      <div className="col mb-4" key={item["id"]}>
        <div className="card shadow-sm">
          <img
            src={item["image"]}
            alt={item["title"]}
            className="card-img-top"
          />
          <div className="card-body">
            <h5 className="card-title">{item["title"]}</h5>
            <p className="card-text">{item["price"]}</p>
            <p className="card-text">{item["description"]}</p>




          </div>
        </div>
      </div>
    </div>
  ))

  let ShowItems = () => {
    return (
      <>
        <div className="album py-5 bg-body-tertiary">
          <div className="container">
            <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-3">

              {cardArray}

            </div>
          </div>
        </div>
      </>
    );
  }

  return (
    <div>
      <section>
                <h1 style={{ textAlign: "center", padding: 5 }}>MAPPY Shop</h1>
                <div className="container">
                <div className="totalPay" style={{ textAlign: "left", padding: 5 }}>
                    Total$:{cartTotal}
                </div>
                <button type="button" className="btn btn-primary" variant="light" >

                </button>
                </div>

            </section>
      <ShowItems/>
      <button onClick={button}>start</button>
    </div>
  );
}

export default App;
