import React, { useState, useEffect } from "react";

function App() {
  const [Items, setItems] = useState([]);
  const [currentView1, setCurrentView1] = useState(0);
  const [creating, setCreating ] = useState(false)
  const changeView = (i) => {
    setCurrentView1(i);
    if (i === 1){
      loadAll()
    }
    if(i === 4){
      console.log('true')
      setCreating(true)
    }
};
  let buttonContent = "";
    switch (currentView1) {
        case 1:
        buttonContent = "Cart";
        break;
        case 2:
        buttonContent = "Browse";
        break;
        default:
        buttonContent = "Cart";
        break;
    }

  let loadAll = async () => {
    await fetch("http://localhost:8081/").then((response) => {
      if (!response.ok) {
        throw new Error("err: ${response.status}");
      }
      response.json().then((data) => {
        console.log(data);
        let tmp = [];
        for (let i of data) {
          tmp.push(i);
        }
        setItems(tmp);
      });
    });
  }

  let update = async (data) => {
    try {
      const response = await fetch(`http://localhost:8081/updateRobot/${data.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      } else {
        const result = await response.json();
        console.log('Update result:', result);
      }
    } catch (error) {
      console.error('Update failed:', error);
    }
  }

  let create = async (data) =>{
    try {
      const response = await fetch(`http://localhost:8081/create`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      } else {
        const result = await response.json();
        console.log('Update result:', result);
      }
    } catch (error) {
      console.error('Update failed:', error);
    }
  }

  let cardArray = Items.map((item) => (
    <div key={item["title"]}>
      <div className="col mb-4" key={item["id"]}>
        <div className="card shadow-sm">
          <img
            src={item["image"]}
            alt={item["title"]}
            className="card-img-top"
          />
          <div className="card-body">
            <h5 className="card-title">{item["title"]}</h5>
            <p className="card-text">{item["price"]}</p>
            <p className="card-text">{item["description"]}</p>
          </div>
        </div>
      </div>
    </div>
  ));

  let ShowItems = () => {

    return (
      <>
        <div className="album py-5 bg-body-tertiary">
          <div className="container">
            <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-3">
              {cardArray}
            </div>
          </div>
        </div>
      </>
    );
  };

  let UpdateItems = ()=>{

    const item = {
      id: 1,
      title: 'Fjallraven - Foldsack No. 1 Backpack, Fits 15 Laptops',
      price: 109.95,
      description: 'Your perfect pack for everyday use and walks in the forest. Stash your laptop (up to 15 inches) in the padded sleeve, your everyday',
      category: "men's clothing",
      image: 'https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg',
      rating: { rate: 3.9, count: 120 }
    }

    let original = "Fjallraven - Foldsack No. 1 Backpack, Fits 15 Laptops"
    update(item)
    return(
    <div>
      hello this is update
      <div><ShowItems/></div>
    </div>)

  }

  let DeleteItems = () =>{
    return (<div> hello this is delete</div>)
  }
  let CreateItems = () =>{
    const [isCreating, setIsCreating] = useState(false);
    const item = {
      id: 99,
      title: 'CREATE 2',
      price: 99,
      description: 'TESITING CREATE',
      category: "TESTING CREATE",
      image: 'https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg',
      rating: { rate: 3.9, count: 120 }
    }
    if(isCreating){
      console.log('creates')
    }
    setCreating(false)

    return (<div> hello this is create</div>)
  }

  return (
    <div>
      <section>
        <h1 style={{ textAlign: "center", padding: 5 }}>MAPPY Shop</h1>
        <div className="container">

          <button
            type="button"
            className="btn btn-primary"
            variant="light"
            onClick={()=>{changeView(1)}}
          >View all</button>
          <button
            type="button"
            className="btn btn-primary"
            variant="light"
            onClick={()=>{changeView(2)}}
          >Update</button>
          <button
            type="button"
            className="btn btn-primary"
            variant="light"
            onClick={()=>{changeView(3)}}
          >Delete</button>
          <button
            type="button"
            className="btn btn-primary"
            variant="light"
            onClick={()=>{changeView(4)}}
          >Create</button>

        </div>
      </section>

      <div id="cart">
        {currentView1 === 1 && <ShowItems />}
        {currentView1 === 2 && <UpdateItems />}
        {currentView1 === 3 && <DeleteItems />}
        {currentView1 === 4 && <CreateItems />}
      </div>
    </div>
  );
}

export default App;
