import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";

function App() {
  const [Items, setItems] = useState([]);
  const [currentView1, setCurrentView1] = useState(0);

  const {
    register: register1,
    handleSubmit: handleSubmit1,
    formState: { errors1 },
    reset: reset1,
  } = useForm();

  const {
    register: register2,
    handleSubmit: handleSubmit2,
    formState: { errors2 },
    reset: reset2,
  } = useForm();

  const {
    register: register3,
    handleSubmit: handleSubmit3,
    formState: { errors3 },
    reset: reset3,
  } = useForm();

  const [printJSON, setPrintJSON] = useState({});
  const [searchItemID, setSearchItemID] = useState(-1);

  const onSubmit3 = async (data) => {
    const datajson = {
      id: parseInt(data.id, 10),
      price: parseFloat(data.price),
    };
    console.log(datajson);
    await searchItem(datajson)
    // Call the create function to post data to the backend
    update(datajson);
  };

  const onSubmit2 = async (data) => {
    console.log("delete runs");
    const datajson = {
      id: data.searchid,
    };
    console.log(datajson);
    await searchItem(datajson)
    //deletor(datajson)
  };
  const onSubmit = (data) => {
    const datajson = {
      id: parseInt(data.id, 10),
      title: data.title,
      price: parseFloat(data.price),
      description: data.description,
      category: data.category,
      image: data.image,
      rating: { rate: parseFloat(data.rate), count: parseInt(data.count) },
    };
    console.log(datajson);

    // Call the create function to post data to the backend
    create(datajson);
  };

  const changeView = (i) => {
    setCurrentView1(i);
    if (i === 1) {
      loadAll();
    }
  };

  let loadAll = async () => {
    await fetch("http://localhost:8081/").then((response) => {
      if (!response.ok) {
